from django.urls import path, re_pathfrom college.views import TeacherListView, MessageView, TeacherDetailView, DepartmentView, DepartmentHomeView, \    DepartmentCourseView, DepartmentDescView, DepartmentTeacherViewapp_name = 'college'       #这里一定要写urlpatterns = [    path('teacher_list/', TeacherListView.as_view(), name='teacher_list'),    path('teacher_detail/', TeacherDetailView.as_view(), name='teacher_detail'),    path('message/', MessageView.as_view(), name='message'),    path('department_list/', DepartmentView.as_view(), name='department_list'),    re_path('home/(?P<department_id>\d+)/', DepartmentHomeView.as_view(), name="department_home"),    re_path('course/(?P<department_id>\d+)/', DepartmentCourseView.as_view(), name="department_course"),    re_path('desc/(?P<department_id>\d+)/', DepartmentDescView.as_view(), name="department_desc"),    re_path('department_teacher/(?P<department_id>\d+)/', DepartmentTeacherView.as_view(), name="department_teacher"),]