from django.urls import path, re_pathfrom college.views import TeacherListView, MessageView, TeacherDetailView, DepartmentView, DepartmentHomeView, \    DepartmentCourseView, DepartmentDescView, DepartmentTeacherView, CollegeView, AddUserAskView, \    DepartmentClassroomViewapp_name = 'college'       #这里一定要写urlpatterns = [    path('teacher_list/', TeacherListView.as_view(), name='teacher_list'),    path('teacher_detail/', TeacherDetailView.as_view(), name='teacher_detail'),    path('message/', MessageView.as_view(), name='message'),    path('department_list/', CollegeView.as_view(), name='department_list'),    path('add_ask/', AddUserAskView.as_view(), name="add_ask"),    re_path('home/(?P<department_id>\d+)/', DepartmentHomeView.as_view(), name="department_home"),    re_path('course/(?P<department_id>\d+)/', DepartmentCourseView.as_view(), name="department_course"),    re_path('desc/(?P<department_id>\d+)/', DepartmentDescView.as_view(), name="department_desc"),    re_path('classroom/(?P<department_id>\d+)/', DepartmentClassroomView.as_view(), name="department_classroom"),    re_path('teacher/(?P<department_id>\d+)/', DepartmentTeacherView.as_view(), name="department_teacher"),]