from django.urls import path, re_pathfrom college.views import TeacherListView, TeacherDetailView, MessageView, CollegeView, AddUserAskView, \    ClassroomHomeView, ClassroomCourseView, ClassroomDescView, ClassroomTeacherView, CollegeListView, \    DepartmentDetailViewapp_name = 'college'       #这里一定要写urlpatterns = [    path('college_list/', CollegeListView.as_view(), name='college_list'),    path('teacher_list/', TeacherListView.as_view(), name='teacher_list'),    path('message/', MessageView.as_view(), name='message'),    path('department_list/', CollegeView.as_view(), name='department_list'),    path('add_ask/', AddUserAskView.as_view(), name="add_ask"),    re_path('department/detail/(?P<department_id>\d+)/', DepartmentDetailView.as_view(), name='department_detail'),    re_path('department/home/(?P<classroom_id>\d+)/', ClassroomHomeView.as_view(), name="classroom_home"),    re_path('department/course/(?P<classroom_id>\d+)/', ClassroomCourseView.as_view(), name="classroom_course"),    re_path('department/desc/(?P<classroom_id>\d+)/', ClassroomDescView.as_view(), name="classroom_desc"),    re_path('department/teacher/(?P<classroom_id>\d+)/', ClassroomTeacherView.as_view(), name="classroom_teacher"),    re_path('teacher/detail/(?P<teacher_id>\d+)/', TeacherDetailView.as_view(), name="teacher_detail"),]