import xadminfrom .models import Course, Video, Data, PPT, Teacherclass CourseAdmin:    list_display = ['course_name', 'course_num', 'course_picture', 'course_intro', 'course_teacher']    search_fileds = ['course_name', 'course_num', 'course_teacher']    list_filter = ['course_name', 'course_num', 'course_teacher']    model_icon = 'fa fa-edit'class VideoAdmin:    list_display = [ 'video_name', 'url', 'learn_times', 'course', 'add_time']    search_fileds = ['video_name', 'learn_times']    list_filter = ['video_name', 'learn_times']    model_icon = 'fa fa-play-circle'class DataAdmin:    list_display = ['data_name', 'data',  'course', 'add_time']    search_fileds = ['data_name', 'data']    list_filter = ['data_name', 'data']    model_icon = 'fa fa-file'class PPTAdmin:    list_display = ['ppt_name', 'ppt', 'course', 'add_time']    search_fileds = ['ppt_name', 'ppt']    list_filter = ['ppt_name', 'ppt']    model_icon = 'fa fa-bars'class TeacherAdmin:    list_display = ['teacher_name', 'teacher_num', 'sex', 'age', 'department', 'phone', 'email', 'picture', 'add_time']    search_fileds = ['teacher_name', 'teacher_num', 'phone', 'email']    list_filter = ['teacher_name', 'teacher_num', 'department', 'phone', 'email']    model_icon = 'fa fa-user-md'xadmin.site.register(Teacher, TeacherAdmin)xadmin.site.register(Course, CourseAdmin)xadmin.site.register(Video, VideoAdmin)xadmin.site.register(Data, DataAdmin)xadmin.site.register(PPT, PPTAdmin)