from django.urls import path, re_pathfrom college import viewsfrom guestbook.views import GuestbookView, PublishGuestbookView, GuestbookDetailView, GuestbookSearchView, ReplyView, \    Reply_ReplyViewapp_name = 'guestbook'       #这里一定要写urlpatterns = [    path('guestbook/', GuestbookView.as_view(), name='guestbook'),    path('publish_guestbook/', PublishGuestbookView.as_view(), name='publish_guestbook'),    re_path('guestbook/detail/(?P<guestbook_id>\d+)/', GuestbookDetailView.as_view(), name='guestbook_detail'),    path('search/', GuestbookSearchView.as_view(), name='search'),    path('guestbook/reply/', ReplyView.as_view(), name='reply'),    path('guestbook/reply/reply/', Reply_ReplyView.as_view(), name='reply_reply')]