def find_parent_reply(rep_tree,reply_obj):    for p, v in rep_tree.items():        if p == reply_obj.parent_reply:  # 找到了父回复            rep_tree[p][reply_obj] ={}        else:            find_parent_reply(rep_tree[p],reply_obj)def create_reply_tree(guestbook_obj):    all_replies = guestbook_obj.reply_set.select_related().order_by('r_time')    reply_tree = {}    for reply in all_replies:        if reply.parent_reply is None:            reply_tree[reply] = {}        else:            find_parent_reply(reply_tree,reply)    return reply_tree