from django.urls import path, re_pathfrom college import viewsfrom platfrom.views import NoticeListView, WorkListView, PlatfromListView, NewsListView, NoticeDetailView, \    NewsDetailView, WorkDetailViewapp_name = 'platfrom'       #这里一定要写urlpatterns = [    path('notice_list/', NoticeListView.as_view(), name='notice_list'),    re_path('notice_detail/(?P<notice_id>\d+)/', NoticeDetailView.as_view(), name="notice_detail"),    path('news_list/', NewsListView.as_view(), name='news_list'),    re_path('news_detail/(?P<news_id>\d+)/', NewsDetailView.as_view(), name="news_detail"),    path('work_list/', WorkListView.as_view(), name='work_list'),    re_path('work_detail/(?P<work_id>\d+)/', WorkDetailView.as_view(), name="work_detail"),    path('platfrom_list/', PlatfromListView.as_view(), name='platfrom_list')]