from django.urls import path, include, re_pathfrom users.views import LoginView, RegisterView, ActiveUserView, ForgetPwdView, ResetView, ModifyPwdViewapp_name = 'users'  #这里一定要写urlpatterns = [path('login/',LoginView.as_view(),name = 'login'),path('register/',RegisterView.as_view(),name = 'register'),path('captcha/', include('captcha.urls')),re_path('active/(?P<active_code>.*)/', ActiveUserView.as_view(), name='user_active'),path('forget/',ForgetPwdView.as_view(),name='forget_pwd'),re_path('reset/(?P<active_code>.*)/', ResetView.as_view(), name='reset_pwd'),path('modify_pwd/', ModifyPwdView.as_view(), name='modify_pwd'),]